')){            ob_start();            extract($param, EXTR_SKIP);            include BASE_DIR.'/SITE/'.$this->pages[0].'/tpl/'.$page.'JS.phtml';            $arr['jsBottom'] = ob_get_clean();            $GLOBALS = array_merge($arr,$GLOBALS);        }        ob_start();        extract($param, EXTR_SKIP);        include BASE_DIR.'/SITE/'.$this->pages[0].'/tpl/'.$page.'.phtml';        $content = ob_get_clean();        return $content;    }}